import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Component Imports
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import EmployeeDashboard from './components/EmployeeDashboard';

// Context Imports
import { AuthProvider, AuthContext } from './context/AuthContext';
import { TaskProvider } from './context/TaskContext';

// --- Private Route Component for Authorization ---
function PrivateRoute({ children, role }) {
  const { user } = useContext(AuthContext); 
  
  if (!user) return <Navigate to="/" />; 
  
  // Redirect if role doesn't match, or navigate to their correct dashboard
  if (role && user.role !== role) {
    return <Navigate to={user.role === 'admin' ? '/admin' : '/employee'} />;
  }

  return children;
}

function App() {
  return (
    // AuthProvider must be the outermost provider
    <AuthProvider>
      <TaskProvider>
        <Router>
          <Routes>
            {/* Public Route: Login */}
            <Route path="/" element={<Login />} />
            
            {/* Admin Protected Route */}
            <Route 
              path="/admin" 
              element={
                <PrivateRoute role="admin">
                  <AdminDashboard />
                </PrivateRoute>
              } 
            />
            
            {/* Employee Protected Route */}
            <Route 
              path="/employee" 
              element={
                <PrivateRoute role="employee">
                  <EmployeeDashboard />
                </PrivateRoute>
              } 
            />

            {/* Catch-all for 404 or redirect */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </Router>
      </TaskProvider>
    </AuthProvider>
  );
}

export default App;
