import { createContext, useState, useEffect, useContext } from 'react';
import API from '../api';
import { AuthContext } from './AuthContext';

export const TaskContext = createContext();

export const TaskProvider = ({ children }) => {
  // Safely access user from AuthContext to prevent initialization errors
  const { user } = useContext(AuthContext); 
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // ⭐️ HELPER FUNCTION TO GET AUTH CONFIG
  const getConfig = () => {
    const token = localStorage.getItem('token');
    if (!token) return {};

    return {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}` // CRITICAL: Attaches JWT for backend validation
      }
    };
  };


  const fetchTasks = async () => {
    // Still ensures we only run if a user is logged in
    if (!user) {
      setTasks([]); 
      return;
    } 

    setLoading(true);
    setError(null);

    try {
      // ⭐️ FIX: Pass the JWT configuration to the GET request
      const res = await API.get('/tasks', getConfig());
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err.response?.data?.message || err);
      setError(err.response?.data?.message || 'Failed to fetch tasks.');
      setTasks([]);
    } finally {
      setLoading(false);
    }
  };
  
  // Fetch tasks whenever the user object changes (e.g., on login/logout)
  useEffect(() => {
    fetchTasks();
  }, [user]); 

  // Function to update task status (used by employee)
  const updateTaskStatus = async (taskId, status) => {
    try {
        // ⭐️ FIX: Pass the JWT configuration to the PATCH request
        const res = await API.patch(`/tasks/${taskId}/status`, { status }, getConfig());
        
        // Update local state to reflect the change immediately
        setTasks(prevTasks => 
            prevTasks.map(task => task._id === taskId ? { ...task, status: res.data.status } : task)
        );
        return res.data;
    } catch (err) {
        console.error('Error updating task status:', err);
        throw err;
    }
  };


  const contextValue = {
    tasks,
    loading,
    error,
    fetchTasks,
    updateTaskStatus,
    setTasks, 
  };

  return (
    <TaskContext.Provider value={contextValue}>
      {children}
    </TaskContext.Provider>
  );
};
